import React from 'react'
import "./css/about.css"
const About = () => {
	return (
		<body>
			<div class="about_home">
				<div class="about_title_text">
					<h1 class="about_title">Über unseren Webplayer</h1>
				</div>
				<div>
					<p class="copyright">Dieser Webplayer wurde von Domenik Rößner und Tom Grothum erstellt, und ist in Kooperation mit der Hochschule Anhalt unter Aufsicht von Toni Barth entstanden.</p>
					<p class="copyright">© 2022 - Diese Anwendung sowie all ihre zugehörigen Logo's und Funktionen stehen in keinster Weise mit www.w2g.tv in Verbindung und sind jediglich für Lehrzwecke zu benutzen.</p>
				</div>
			</div>
		</body>
	);
};

export default About;
